import axios from 'axios';

const API_URL = 'http://localhost:8000';

const api = {
  // Endpoints de base
  getStatus: async () => {
    const response = await axios.get(`${API_URL}/status`);
    return response.data;
  },

  // Gestion des données
  getData: async (filename = null, format = 'json') => {
    let url = `${API_URL}/data`;
    if (filename) {
      url += `?filename=${filename}&format=${format}`;
    }
    const response = await axios.get(url);
    return response.data;
  },

  addData: async (data, filename, format = 'json') => {
    const response = await axios.post(`${API_URL}/data/${filename}`, data);
    return response.data;
  },

  // Génération de blagues
  getJoke: async () => {
    const response = await axios.get(`${API_URL}/joke`);
    return response.data;
  }
};

export default api;
