import React, { useState } from 'react';
import { 
  Button, 
  Dialog, 
  DialogTitle, 
  DialogContent, 
  DialogActions,
  Typography,
  Paper
} from '@mui/material';
import api from '../services/api';

function Status() {
  const [open, setOpen] = useState(false);
  const [statusData, setStatusData] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleClickOpen = async () => {
    setOpen(true);
    setLoading(true);
    try {
      const data = await api.getStatus();
      setStatusData(data);
    } catch (error) {
      console.error('Erreur lors de la récupération du statut:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleClose = () => {
    setOpen(false);
  };

  return (
    <div>
      <Button 
        variant="contained" 
        color="primary" 
        onClick={handleClickOpen}
        sx={{ mb: 2 }}
      >
        Vérifier le statut de l'API
      </Button>

      <Dialog open={open} onClose={handleClose}>
        <DialogTitle>Statut de l'API</DialogTitle>
        <DialogContent>
          {loading ? (
            <Typography>Chargement...</Typography>
          ) : statusData ? (
            <Paper sx={{ p: 2, mt: 1 }}>
              <Typography variant="body1">
                <strong>Statut:</strong> {statusData.status}
              </Typography>
              <Typography variant="body1">
                <strong>Dernière mise à jour:</strong> {new Date(statusData.timestamp).toLocaleString()}
              </Typography>
            </Paper>
          ) : (
            <Typography color="error">
              Erreur lors de la récupération du statut
            </Typography>
          )}
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose} color="primary">
            Fermer
          </Button>
        </DialogActions>
      </Dialog>
    </div>
  );
}

export default Status;
