import axios from 'axios';

const API_URL = 'http://localhost:8000';

const api = {
  // Endpoints de base
  getStatus: async () => {
    const response = await axios.get(`${API_URL}/status`);
    return response.data;
  },

  // Gestion des données
  getData: async (format = 'json') => {
    const response = await axios.get(`${API_URL}/data?format=${format}`);
    return response.data;
  },

  addData: async (data, format = 'json') => {
    const response = await axios.post(`${API_URL}/data?format=${format}`, data);
    return response.data;
  },

  // Génération de blagues
  getJoke: async () => {
    const response = await axios.get(`${API_URL}/joke`);
    return response.data;
  }
};

export default api;
