import React, { useState } from 'react';
import AppBar from '@mui/material/AppBar';        // Barre de navigation Material-UI
import Toolbar from '@mui/material/Toolbar';      // Conteneur pour les éléments de la barre
import Typography from '@mui/material/Typography'; // Pour le texte stylisé
import StorageIcon from '@mui/icons-material/Storage'; // Icône de stockage
import Button from '@mui/material/Button';
import Dialog from '@mui/material/Dialog';
import DialogTitle from '@mui/material/DialogTitle';
import DialogContent from '@mui/material/DialogContent';
import DialogActions from '@mui/material/DialogActions';
import Paper from '@mui/material/Paper';
import Box from '@mui/material/Box';
import CircularProgress from '@mui/material/CircularProgress';
import api from '../services/api';

function Header() {
  const [open, setOpen] = useState(false);
  const [statusData, setStatusData] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleClickOpen = async () => {
    setOpen(true);
    setLoading(true);
    try {
      const data = await api.getStatus();
      setStatusData(data);
    } catch (error) {
      console.error('Erreur lors de la récupération du statut:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleClose = () => {
    setOpen(false);
  };

  return (
    <>
      <AppBar position="static">                    
        <Toolbar>
          <StorageIcon sx={{ mr: 2 }} />            
          <Typography variant="h6" component="div" sx={{ flexGrow: 1 }}>
            API de Gestion de Données
          </Typography>
          <Button 
            color="inherit" 
            onClick={handleClickOpen}
            sx={{ 
              '&:hover': {
                backgroundColor: 'rgba(255, 255, 255, 0.1)'
              }
            }}
          >
            Statut API
          </Button>
        </Toolbar>
      </AppBar>

      <Dialog 
        open={open} 
        onClose={handleClose}
        maxWidth="sm"
        fullWidth
      >
        <DialogTitle sx={{ 
          backgroundColor: 'primary.main',
          color: 'white',
          py: 2
        }}>
          Statut de l'API
        </DialogTitle>
        <DialogContent sx={{ mt: 2 }}>
          {loading ? (
            <Box sx={{ display: 'flex', justifyContent: 'center', p: 3 }}>
              <CircularProgress />
            </Box>
          ) : statusData ? (
            <Paper sx={{ p: 3, mt: 1 }}>
              <Typography variant="body1" sx={{ mb: 2 }}>
                <strong>Statut:</strong>{' '}
                <Box 
                  component="span" 
                  sx={{ 
                    color: statusData.status === 'ok' ? 'success.main' : 'error.main',
                    fontWeight: 'bold'
                  }}
                >
                  {statusData.status}
                </Box>
              </Typography>
              <Typography variant="body1">
                <strong>Dernière mise à jour:</strong>{' '}
                {new Date(statusData.timestamp).toLocaleString()}
              </Typography>
            </Paper>
          ) : (
            <Typography color="error" sx={{ p: 2 }}>
              Erreur lors de la récupération du statut
            </Typography>
          )}
        </DialogContent>
        <DialogActions sx={{ p: 2 }}>
          <Button 
            onClick={handleClose} 
            variant="contained" 
            color="primary"
            sx={{ px: 3 }}
          >
            Fermer
          </Button>
        </DialogActions>
      </Dialog>
    </>
  );
}

export default Header;
