import React from 'react';
import { ThemeProvider, createTheme } from '@mui/material/styles'; // Gestion du thème
import CssBaseline from '@mui/material/CssBaseline';              // Reset CSS
import Container from '@mui/material/Container';                  // Conteneur responsive
import Box from '@mui/material/Box';                             // Conteneur flexible
import Header from './components/Header';                        // Notre composant Header
import Status from './components/Status';  // Ajoutez cette ligne avec les autres imports

// Configuration du thème de l'application
const theme = createTheme({
  palette: {
    mode: 'light',              // Thème clair
    primary: {
      main: '#1976d2',         // Couleur principale (bleu)
    },
    secondary: {
      main: '#dc004e',         // Couleur secondaire (rouge)
    },
  },
});

function App() {
  return (
    <ThemeProvider theme={theme}>    
      <CssBaseline />               
      <Box sx={{ flexGrow: 1 }}>    
        <Header />                  
        <Container maxWidth="lg" sx={{ mt: 4, mb: 4 }}>  
          <Box sx={{ p: 2, backgroundColor: 'white', borderRadius: 1 }}>
            <h1>Bienvenue sur l'API de Gestion de Données</h1>
            <p>Votre interface de gestion est en cours de chargement...</p>
            <Status />  {/* Ajoutez cette ligne */}
          </Box>
        </Container>
      </Box>
    </ThemeProvider>
  );
}

export default App;
